add_shortcode( 'bmp_svg', '_s_get_svg' );

function _s_get_svg( $atts = array() ) {
    $atts = shortcode_atts( array(
		'type' => '',
	), $atts, 'svg' );
    
    if( !isset( $atts['type'] ) ) {
        return;   
    }
    
    $svg = get_svg( $atts['type'] );
    
    return $svg;
}